import React from 'react';
import './Item.css'

class item extends React.Component{

    
    constructor(){
        //super(props);

        const { innerRef, ...props } = this.props;

        this.state = {
            stars: []
        };
    }

    componentDidMount(){
        this.setState({
            stars: Array(parseInt(this.props.rating)).fill(0)
        });
    }
    

    render(){

        return(
            <div className="item">

                <div ref={innerRef} {...props} className="image"><img src={'img/' + this.props.image} width="100%" alt="" /></div>
                <div className="title">{this.props.title}</div>
                <div className="rating">
                Rating
                    <p>
                    {
                        this.state.stars.map(x => 
                            <img src="img/star.png" width="32" alt="" />
                            )
                    }
                    </p>
                </div>
                <div className="actions"></div>
            </div>
        );
    }
}
const itemForwardingRef = React.forwardRef((props, ref) => (
    <item {...props} innerRef={ref}/>
  ));
  
  const App = () => (
    <itemForwardingRef ref={ref => ref && ref.focus()} />
  )

export default item;
