import React, { useRef, useEffect } from 'react';
import './Item.css'
import { gsap } from "gsap";

//class item extends React.Component{

    /*constructor(props){
        super(props);

        this.state = {
            stars: []
        };
    }

    componentDidMount(){
        this.setState({
            stars: Array(parseInt(this.props.rating)).fill(0)
        });
    }*/

    const item = React.forwardRef((props) => {
        const imageRef = useRef(null);
        const stars = [];

        useEffect(() => {
         
            stars = Array(parseInt(props.rating)).fill(0);

            gsap.from(imageRef.current, {
                duration: .3,
                y: -200,
                scale: 0,
                stagger: 0.5
            });
     
        }, []);

        return(
            <div className="item">

                <div ref={imageRef} className="image"><img src={'img/' + props.image} width="100%" alt="" /></div>
                <div className="title">{props.title}</div>
                <div className="rating">
                Rating
                    <p>
                    {
                        stars.map(x => 
                            <img src="img/star.png" width="32" alt="" />
                            )
                    }
                    </p>
                </div>
                <div className="actions"></div>
            </div>
        );
    });
//}

export default item;
