import './App.css';
import Menu from './Menu'
import Lista from './Lista'
import React from 'react';

class App extends React.Component {

  constructor(props){
    super(props);

    this.state = {
      books:[
        {id:1, rating: 4.4, title:'El Mensaje de Pandora', image: 'pandora.png' },
        {id:2, rating: 5, title:'La Divina Comedia', image: 'divina.jpg' },
        {id:3, rating: 5, title:'El Arte de la Guerra', image: 'guerra.jpg' },
        {id:4, rating: 4.5, title:'Stop Over Thinking', image: 'over thinking.jpg' },
        {id:5, rating: 4.8, title:'The Shining', image: 'shining.jpg' },
      ]
    };
  }

  render() {
    return (
      <div className="App">
        <Menu title="C-Books"/>
        <Lista items={this.state.books} />
      </div>
    );
  }
}


export default App;
