import './App.css';
import Menu from './Menu'
import Lista from './Lista'
import React from 'react'
import Data from './datos-libros.json'

class App extends React.Component {

  constructor(props){
    super(props);

    this.state = {
      copyData: []
    };
  }

  componentDidMount(){
    this.initData();
  }

  initData = () => {
    this.setState((props) => ({
      copyData: [...Data]
    }));
  }

  onSearch = (query) => {
    if(query == ''){
      this.initData();
    }else{
      const temp = [...Data];
      let res = [];

      temp.forEach(item => {
        if(item.title.toLowerCase().indexOf(query) > -1){
          res.push(item);
        }
      });
      this.setState({copyData: [...res] });
    }
  }

  render() {

    return (
      <div className="App">
        <Menu title="C-Books" onsearch={this.onSearch} />
        <Lista />
      </div>
    );
  }
}


export default App;
