import React from 'react'
import './Item.css'

class item extends React.Component{

    constructor({image,title,rating}){
        super({image,title,rating});

        this.state = {
            stars: []
        };
    }

    componentDidMount(){
        this.setState({
            stars: Array(parseFloat(this.rating)).fill(0)
        });
    }

    render(){
        return(
            <div className="item">
                <div className="image"><img src={'img/' + this.image} width="100%" alt="" /></div>
                <div className="title">{this.title}</div>
                <div className="rating">
                    <p>
                    {
                        this.state.stars.map(x => 
                            <img src="img/star.png" width="32" atl="" />
                            )
                    }
                    </p>
                    Rating:
                    <select defaultValue={this.rating}>
                        <option value="1">1</option>
                        <option value="2">2</option>
                        <option value="3">3</option>
                        <option value="4">4</option>
                        <option value="5">5</option>
                    </select>
                </div>
                <div className="actions">
                    <button>Eliminar</button>
                </div>
            </div>
        );
    }
}

export default item;
